    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        // fungsi untuk mengubah text username dan password berwana merah jika salah
        if("".equals(jTextField1.getText())){
                jTextField1.setBackground(Color.RED);
            }
        else{
              jTextField1.setBackground(Color.WHITE);
        }
         if("".equals(jPasswordField1.getText())){
                jPasswordField1.setBackground(Color.RED);
            }
         else{
              jPasswordField1.setBackground(Color.WHITE);

        }
         String sql = "select from admin where username=? and password=?";
         try{
         PreparedStatement pst=conn.prepareStatement(sql);
         pst.setString(1,jTextField1.getText());
         pst.setString(2,jPasswordField1.getText());
         rs=pst.executeQuery();
         if(rs.next()){
             rs.close();
             pst.close();
             
             setVisible(false);
             Welcome mainAdmin = new Welcome();
             mainAdmin.setVisible(true);
         }
         else{
             JOptionPane.showMessageDialog(null,"Incorrect Username and Password");
         }
         }catch(Exception e){
             JOptionPane.showMessageDialog(null, e);
                 }finally{
                 try{
                 rs.close();
                 pst.close();
                 }catch(Exception ex){
                 }
         
    }//GEN-LAST:event_jButton1ActionPerformed
    }

